import axios from 'axios';

const Post = ({ id }) => <h1>you are looking at post #{id}</h1>;

Post.getInitialProps = async ({ query }) => {
  const res = await axios.get(
    'https://jsonplaceholder.typicode.com/comments?postId=3'
  );
  const { data } = res;
  return { data };
  console.log(data[0]);
};

export default Post;
